mapper.select:
SELECT t1.id, t2.id staff_id, t1.account_name, t2.staff_name, t2.staff_num, t3.department_name, t1.permissions, t4.account_name modify_emp_name, t5.staff_name modifier, t1.modify_time, t1.account_state FROM t_account t1 LEFT JOIN t_staff t2 ON (t1.id = t2.account_id) LEFT JOIN t_account t4 ON (t1.modify_emp = t4.id) LEFT JOIN t_department t3 ON (t2.department_id = t3.id) LEFT JOIN t_staff t5 ON t1.modifier = t5.id WHERE t1.account_state != 0
and t1.account_state != 3
and t1.id = #{userId}
and t2.department_id in #{item}
and t1.account_name like concat('%',#{accountName},'%')
and t2.staff_name like concat('%',#{staffName},'%')
and t2.staff_num like concat('%',#{staffNum},'%')
and t1.permissions = #{permissions}
and t3.id = #{departmentId}
and t1.staff_id != 0 and t1.staff_id is not null
and (t1.staff_id = 0 or t1.staff_id is null)
and t1.account_state = #{accountState}
and t1.id not in #{item2}
and t1.account_state = 1 or t1.account_state = 2
ORDER BY modify_time DESC

select a.id role_id, c.id account_id, a.role_name, a.business_line, a.role_status, c.account_name, c.account_state, d.staff_num, d.staff_name, e.department_name from t_role_info a LEFT JOIN t_r_account_role b on b.role_id = a.id LEFT JOIN t_account c ON b.account_id = c.id LEFT JOIN t_staff d on c.staff_id = d.id LEFT JOIN t_department e on d.department_id = e.id where 1=1
and role_name = #{roleName}
and business_line like "%"#{businessLine,jdbcType=VARCHAR}"%"
and account_name = #{accountName}
and staff_num = #{staffNum}
and d.staff_name = #{staffName}
and role_status = #{roleStatus}
and account_state = #{accountState}
and department_name = #{departmentName}
ORDER BY role_id DESC

mapper.update:
update t_account t1
set t1.account_state=#{status}
where t1.id = #{id}

mapper.select:
SELECT department_id
FROM t_r_department_power
WHERE account_id = #{accountId}

mapper.update:
update t_account t1
set t1.modify_emp=#{a.modifyEmpId},t1.modify_time=#{a.modifyTime},t1.modifier=#{a.modifierId}
where t1.id = #{a.id}

mapper.select:
select account_name
from t_account
WHERE account_name = #{AccountName}

mapper.update:
