mapper.select:
SELECT
id,
company_name,
credit_code,
company_type,
company_nature,
company_mark,
status companyStatus,
modify_emp,
modify_time
FROM t_company


    AND company_name =#{companyName}


    AND credit_code =#{creditCode}


    AND company_type =#{companyType}


    AND company_nature =#{companyNature}


    AND status =#{companyStatus}


    AND modify_time>#{startTime}


    AND modify_time<#{endTime}



ORDER BY id DESC

select count(*)
from  t_company
where credit_code=#{creditCode}
