mapper.select:
SELECT
t1.id,
t1.company_name,
t1.credit_code,
t1.company_type,
t1.company_nature,
t1.company_mark,
t1.status companyStatus,
t2.account_name modifyName,
t1.modify_time modifyTime
FROM t_company t1,t_account t2

    t1.modify_emp=t2.id

    AND t1.company_name =#{companyName}


    AND t1.credit_code =#{creditCode}


    AND t1.company_type =#{companyType}


    AND t1.company_nature =#{companyNature}


    AND t1.status =#{companyStatus}


    AND t1.modify_time>#{startTime}


    AND t1.modify_time<#{endTime}



ORDER BY id DESC

mapper.insert:
select @@IDENTITY as id
insert into t_company( company_name, credit_code, company_type, business_start_time, business_deadline, company_address, business_scope, legal_person, registered_capital, establish_time, registered_institution, issue_date, registered_status, liscense_path, company_nature, registration_address, bank_name, bank_account, telephone, company_mark, status, create_emp, create_time, modify_emp, modify_time, remark
) values( #{companyName}, #{creditCode}, #{companyType}, #{businessStartTime}, #{businessDeadline}, #{companyAddress}, #{businessScope}, #{legalPerson}, #{registeredCapital}, #{establishTime}, #{registeredInstitution}, #{causeTime}, #{registeredStatus}, #{liscensePath}, #{companyNature}, #{registrationAddress}, #{bankName}, #{bankAccount}, #{telephone}, #{companyMark}, #{companyStatus}, #{createEmp}, now(), #{modifyEmp}, now(), #{remark} )

mapper.select:
select count(*)
from  t_company
where credit_code=#{creditCode}

select
    T.id,
    T.company_name,
    T.credit_code,
    T.company_type,
    T.company_nature,
    T.company_mark,
    T.status companyStatus,
    t1.account_name modifyName,
    T.modify_time
from t_company T,t_account t1

        T.id not in ( select t2.company_id from t_r_department_company t2 where t2.department_id = #{departmentId} and t2.status=1 )


            AND T.company_name =#{companyName}


            AND T.credit_code =#{creditCode}


            AND T.company_type =#{companyType}


            AND T.company_nature =#{companyNature}


            AND T.status =#{companyStatus}


            AND T.modify_time>#{startTime}


            AND T.modify_time<#{endTime}

            and T.modify_emp=t1.id

mapper.update:
update t_r_department_company t1 set t1.status = #{status}, t1.modify_emp=#{modifyEmp}, t1.modify_time=now()
AND t1.department_id =#{departmentId}
AND t1.company_id =#{companyId}

mapper.select:
select
count(*)
from t_r_department_company t1

    t1.status=2

        AND t1.department_id =#{departmentId}


        AND t1.company_id =#{companyId}

SELECT  url
from t_license
 WHERE id=#{id}
