mapper.update:
UPDATE t_department
SET status=0
WHERE id=#{id}

UPDATE t_department
SET upper_department_No=#{upperDepartmentNo}
WHERE id=#{id}

mapper.select:
SELECT count(*) FROM t_department
WHERE department_no=#{departmentNo}

    AND id!=#{id}

select count(*) from t_department
where workplace=#{workplace} and upper_department_no=#{upperDepartmentNo}

    and id!=#{id}

select count(*) from t_department
where department_no=#{upperDepartmentNo}

    and status=1

        select T.staff_name from t_staff T
        left join t_account T2 on T2.staff_id=T.id
		where T2.id=#{accountId}

SELECT A.id, A.department_no, A.workplace, A.department_name, A.staff_id, A.staff_name, A.city_id, A.address, A.telephone, A.email, A.landline, A.level, A.upper_department_no, A.support_business, A.department_type, A.status, A.create_emp, A.create_time, A.modify_emp, A.modify_time, A.remark, A.city_name, A.company_name FROM( SELECT T.id, T.department_no, T.workplace, T.department_name, T.staff_id, T.staff_name, T.city_id, T.address, T.telephone, T.email, T.landline, T.level, T.upper_department_no, T.support_business, T.department_type, T.status, T.create_emp, T.create_time, T.modify_emp, T.modify_time, T.remark, T1.city_name, group_concat(T3.company_name SEPARATOR';') as company_name from t_department T LEFT JOIN t_b_city T1 on T.city_id=T1.city_id LEFT JOIN t_r_department_company T2 on T.id = T2.department_id LEFT JOIN t_company T3 ON T2.company_id=T3.id
AND T.staff_id=#{staffId} AND T.department_name=#{departmentName} AND T.staff_name=#{staffName} AND T.staff_id=#{staffId} AND T.telephone=#{telephone} AND T1.city_name=#{cityName} AND T.level=#{level} AND T.upper_department_no=#{upperDepartmentNo}
AND T.status=#{status}
AND T.department_type=#{departmentType}
GROUP BY T.id) AS A GROUP BY A.status,A.create_time ORDER BY A.status DESC ,A.create_time DESC
