mapper.update:
UPDATE t_staff
SET account_id = #{accountId}
,modify_time = NOW()
,modify_emp = #{modifyEmp}

,staff_email = #{secretEmail}

WHERE id = #{staffId};

mapper.select:
SELECT t1.id id, t1.staff_num staffNum, t3.account_name, t1.account_id accountId, t1.staff_name staffName, t1.staff_sex staffSex, t1.staff_telephone staffTelephone, t1.staff_email staffEmail, t1.department_id departmentId, t1.is_dimission isDimission, t1.create_time createTime, t2.upper_department_no upperDepartmentNo, t2.department_name departmentName, t4.department_name upperDepartmentName FROM t_staff t1 LEFT JOIN t_account t3 on (t3.id=t1.account_id) ,t_department t2,t_department t4 where t1.department_id = t2.id and t2.upper_department_no=t4.department_no
AND (t1.account_id = '' or t1.account_id IS NULL)
AND t1.staff_num =#{satff_num}
AND t1.staff_name =#{staff_name}
AND t3.account_name=#{accountName}
AND t1.is_dimission =#{is_dimission}
AND t1.is_dimission =#{upper_department_no}
ORDER BY createTime DESC

mapper.update:
update t_staff t1
set t1.status=0
where t1.id = #{id}
