mapper.delete:
delete from t_r_account_power
where 1=1
and account_id = #{accountId}
and power_id = #{powerId}

mapper.select:
select
 T.account_name,
 T.staff_id,
 T1.staff_num,
 T1.staff_name,
 T4.department_name,
 T3.power_name,
 T.account_state
 from t_account T JOIN t_staff T1 ON T.staff_id=T1.id
   JOIN t_r_account_power T2 ON T.id=T2.account_id
   JOIN t_power T3 ON T2.power_id=T3.power_id
   JOIN t_department T4 on T1.department_id=T4.id


           AND T.account_name=#{accountName}


           AND T1.staff_num=#{staffNum}


           AND T1.staff_name=#{staffName}


           AND T4.department_name=#{departmentName}


           AND T3.power_name=#{powerName}


           AND T.account_state=#{accountState}

SELECT
  t1.id role_id,
  t1.role_name,
  t1.business_line,
  t1.role_status,
  t1.approver_account_name,
  t1.approver_staff_num,
  t1.approver_staff_name,
  t1.approver_department_name,
GROUP_CONCAT(t3.power_name separator '-') power_name
from t_r_role_power t2 LEFT JOIN t_power t3 on t2.power_id=t3.power_id
     join t_role_info t1 on t1.id=t2.role_info_id

    and t1.id=#{roleId}
    and t1.role_name=#{roleName}
    and t1.business_line=#{businessLine}
    and t1.role_status=#{roleStatus}
    and
        t1.approver_account_name=#{approverAccountName}
    and
        t1.approver_staff_num=#{approverStaffNum}
    and
        t1.approver_department_name=#{approverDepartmentName}
    and t3.power_name=#{powerName}

GROUP BY t1.id
