mapper.select:
SELECT t1.city_id cityID, t1.international_code regionCode, t1.country_code countryCode, t1.area_code regionAreaCode, t1.city_name_pinyin regionPinyin, t1.city_name regionName, t1.`level` regionLevel, t1.upper_city_id upperRegionID, t1.upper_city_name upperRegion, t1.`status` regionStatus, t1.create_emp createEmp, t1.create_time createTime, t1.modify_emp modifyEmp, t1.modify_time modifyTime, t2.account_name modifyEmpName, t1.remark remark FROM t_b_city t1,t_account t2 WHERE t1.`level` = 2 AND t1.modify_emp=t2.id
AND t1.international_code LIKE #{regionCode}"%"
AND t1.city_name LIKE #{regionName}"%"
AND t1.status =#{regionStatus}
AND t1.upper_city_name LIKE #{upperRegion}"%"
AND t1.upper_city_id = #{upperRegionID}

SELECT t1.city_id cityID, t1.international_code regionCode, t1.country_code countryCode, t1.area_code regionAreaCode, t1.city_name_pinyin regionPinyin, t1.city_name regionName, t1.`level` regionLevel, t1.upper_city_id upperRegionID, t1.upper_city_name upperRegion, t1.`status` regionStatus, t1.create_emp createEmp, t3.account_name createEmpName, t1.create_time createTime, t1.modify_emp modifyEmp, t1.modify_time modifyTime, t2.account_name modifyEmpName, t1.remark remark FROM t_b_city t1,t_account t2,t_account t3 WHERE t1.`level` = #{regionLevel} AND t1.modify_emp = t2.id AND t1.create_emp = t3.id
AND t1.city_id = #{cityID}
AND t1.international_code LIKE #{regionCode}"%"
AND t1.city_name LIKE #{regionName}"%"
AND t1.status =#{regionStatus}

mapper.update:
update t_b_city t1
set t1.international_code=#{regionCode},t1.city_name=#{regionName},t1.city_name_pinyin=#{regionPinyin},t1.status=#{regionStatus},t1.modify_emp=#{modifyEmp},t1.modify_time=#{modifyTime}
where t1.city_id = #{cityID}

update t_b_city t1
set t1.upper_international_code=#{regionCode},t1.upper_city_name=#{regionName},t1.modify_emp=#{modifyEmp},t1.modify_time=#{modifyTime}
where t1.upper_city_id = #{cityID}

mapper.select:
		SELECT T.city_name FROM t_b_city T JOIN t_department T1 on T.city_id=T1.city_id
         WHERE T1.id=#{id} OR T1.upper_department_no =(
	        SELECT T2.department_no FROM t_department T2
		      WHERE T2.id=#{id}
          );

	SELECT city_id cityID
 	FROM  t_b_city t1
 	WHERE t1.international_code=#{regionCode}

	SELECT international_code RegionCode
 	FROM  t_b_city t1
 	WHERE t1.city_id=#{cityID}
