mapper.select:
select t1.id roleId, t1.role_name roleName, t1.approver_account_name accountNum, t1.approver_staff_num staffNum, t1.approver_staff_name staffName, t1.approver_department_name departmentName, t1.role_status roleStatus, t1.description description from t_role_info t1 where t1.role_status=1
AND t1.role_name = #{role_name}
AND t1.id = #{id}

mapper.update:
update t_role_info t1
set t1.role_status=0
where t1.id = #{id}
